import styled from "styled-components";
import { useState } from "react";
import style from "./Form.module.css";
import { Link } from "react-router-dom";
import validate from "./validate";
// const regEx = /^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{3})+$/;
const StyledForm = styled.form`
  background-color: #1749;
  margin: auto;
  margin-top: 30px;
  padding: 25px;
  width: 300px;
  height: 150px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-family: Calibri, sans-serif;
  font-size: 16px;
  font-weight: bold;
  outline: none;
  border: 3px solid lightgreen;
  box-shadow: 7px 7px 5px lightgreen;
  border-radius: 10px;
`;

const Input = styled.input`
  width: 170px;
  background-color: #1149;
  color: black;
  border: none;
  padding: 10px 15px 5px;
  cursor: pointer;

  &:hover {
    background-color: lightgreen;
    cursor: mouse;
    transition: 0.5s;
  }
  &:focus {
    outline: none;
    width: 200px;
    background-color: #1739;
  }
`;

const Button = styled.button`
  width: 50px;
  height: 20px;
  background-color: #1749;
  color: black;
  border: none;
  cursor: pointer;

  &:hover {
    background-color: lightgreen;
    cursor: pointer;
    width: 70px;
    transition: 0.5s;
  }
  &:focus {
    outline: none;
    transition: 0.5s ease all;
    width: 70px;
  }
  &:active {
    background-color: #6b6e6f;
    width: 50px;
  }
`;

const ButtonBack = styled.button`
  width: 50px;
  height: 20px;
  border: none;
  background-color: #1749;
  color: black;
  cursor: pointer;

  &:hover {
    background-color: lightgreen;
    cursor: pointer;
    width: 70px;
    transition: 0.5s;
  }
  &:focus {
    outline: none;
    transition: 0.5s ease all;
    width: 70px;
  }
  &:active {
    background-color: #6b6e6f;
    width: 50px;
  }
`;

const SpanError = styled.span`
  color: red;
`;

// const validate = (userData, setErrors, errors) => {
//   if (!userData.email) {
//     setErrors({ ...errors, email: "Invalid mail" });
//   } else {
//     if (regEx.test(userData.email)) setErrors({ ...errors, email: "" });
//     else setErrors({ ...errors, email: "Invalid mail" });
//   }
// };

export default function Form() {
  const [userData, setData] = useState({
    email: "joaquinbotto2@hotmail.com",
    password: "password123",
  });

  const [errors, setErrors] = useState({
    email: "",
    password: "",
  });

  const submitHandler = (event) => {
    event.preventDefault();
    if (!errors.email || !errors.password) {
      return;
    }
  };

  const handleChange = (event) => {
    const property = event.target.name;
    const value = event.target.value;

    setData({ ...userData, [property]: value });
    validate({ ...userData, [property]: value }, setErrors, errors);
  };

  return (
    <StyledForm onSubmit={submitHandler}>
      <label htmlFor="email">Email:</label>
      <br />
      <Input
        type="text"
        name="email"
        placeholder="Your email"
        value={userData.email}
        onChange={handleChange}
        required
        // className={!errors.email ? style.success : style.miss}
      />
      <SpanError>{errors.email}</SpanError>
      <label htmlFor="password">Password:</label>
      <Input
        type="password"
        name="password"
        placeholder="Your password"
        value={userData.password}
        onChange={handleChange}
        required
        // className={!errors.password ? style.success : style.miss}
      />
      <SpanError>{errors.password}</SpanError>
      <hr />
      <Link to="/home">
        <Button type="submit" name="button">
          Log In
        </Button>
      </Link>
      <hr />
    </StyledForm>
  );
}
